import { SETADDRESS, SHOW, HIDE, CLEAR } from 'actionTypes'

const initialState = {
    value: '',
    visi: false
}

// you should separate out your reducers as much as possible to help with scaling.
// this is just an example showing how its done. see searchBox reducer for most of the reducing stuff

export default function AddressReducer(state = initialState, action) {
    switch (action.type) {
        case SETADDRESS:
            return { ...state, value: action.address}
        case SHOW:
            if (action.component === 'AddressInput') {
                return { ...state, visi: true}
            } else {
                return { ...state, visi: false}
            }
        case HIDE:
        if (action.component === 'AddressInput') {
            return { ...state, visi: false};
        };
        return state;
        case CLEAR: 
            return {...state, value: ''}
        default:
            return state
    }
}