import CustomerReducer from './CustomerReducer'
import EmailReducer from './EmailReducer'
import { combineReducers } from 'redux'
import SearchReducer from './SearchReducer'
import AddressReducer from './AddressReducer'


// combineReducers is a little bit magical. it does special stuff behind the scenes that isn't always obvious. 
// read this https://redux.js.org/api/combinereducers


// Using javascript generatator functions (function*) you might be able to simplify duplicate reducers tremenduously.
export default combineReducers({ 
    CustomerInput: CustomerReducer,
    EmailInput: EmailReducer,
    AddressInput: AddressReducer,
    Search: SearchReducer
})
